mapper.select:
SELECT distinct ${column} FROM ${table}
where ${column} is not null and ${column} !=''

	and level=#{level}


	and parent=#{parent}

SELECT count(*) as total,b.${column2} as name
FROM ${table} a left join ${table1} b on a.${column1} = b.code_index
WHERE b.dic_code = '${column1}' GROUP BY a.${column1}

SELECT count(1) FROM ${table} 
	where 1=1 


		     and ${column} >= #{remindstart}


		     and ${column} <= #{remindend}




		     and ${column} >= str_to_date(#{remindstart},'%Y-%m-%d')


		     and ${column} <= str_to_date(#{remindend},'%Y-%m-%d')
